CREATE OR REPLACE FUNCTION get_address(person_id IN NUMBER) RETURN VARCHAR2 IS person_details VARCHAR2(130);
BEGIN
SELECT 'Name-'||person.first_name||' '|| person.last_name||',City-'|| address.city ||', State-'||address.state||',Country-'||address.country||', ZIP Code-'||address.zip_code INTO person_details FROM person_info person, person_address address WHERE person.id = person_id AND address.person_id = person.id;
RETURN(person_details);
END get_complete_address;

CREATE OR REPLACE FUNCTION fsum(n1 IN NUMBER, n2 IN NUMBER) RETURN NUMBER IS
n3 number(8);
begin
n3 :=n1+n2;
return n3;
end;

CREATE OR REPLACE FUNCTION fsub(n1 IN NUMBER, n2 IN NUMBER) RETURN NUMBER IS
n3 number(8);
begin
n3:=n1-n2;
return n3;
end fsub;
